import React from 'react';
import Modal from './Modal'

class Main extends React.Component {
	constructor () {
		super();

		this.state = {
			visible: false
		}

		this.showModal = this.showModal.bind(this);
		this.hideModal = this.hideModal.bind(this);
	}
	showModal () {
		this.setState({visible: true});
	}
	hideModal () {
		this.setState({visible: false});
	}



	render () {
		return (
			<section>
				<nav role="navigation">
					<button type="button" onClick={this.showModal} className="btn">
						Remove
					</button>
				</nav>

				<Modal visible={this.state.visible}>
					<h3 className="dialog__title">
						Are you sure you want to delete the item?
					</h3>
					<button onClick={this.hideModal} type="button" className="btn btn--close dialog__btn">
						Cancel
					</button>
					<button onClick={() => this.props.onclick(this.props.student)} type="button" className="btn">
						Yes!
					</button>
				</Modal>
			</section>
		);
	}
  };

  export default Main
